import { useState } from "react"

const InputParty = ({peoples,setPeoples}) =>{
    const [ name ,setName] = useState("")
    const [ age ,setAge] = useState(0)

    return(
        <>
            <input placeholder="Seu nome" onChange={(e) => setName(e.target.value)} />
            <input placeholder="Sua idade" onChange={(e) => setAge(Number(e.target.value))} />
            <button onClick={() => setPeoples(...peoples, {name:name,age:age})}>cadastrar</button>

        </>
    )
}

export default InputParty
